//line based drawing
void drawTextureByLine(struct line texture[], uint16_t textureLength, uint16_t offset_x, uint16_t offset_y) {
	int i;
	for(i = 0; i < textureLength; i++) {
		LCD_DrawLine(texture[i].x0+offset_x, texture[i].y0+offset_y, texture[i].x1+offset_x, texture[i].y1+offset_y, texture[i].color);
	}
}
void eraseTextureByLine(struct line texture[], uint16_t textureLength, uint16_t offset_x, uint16_t offset_y) {
	uint16_t bColor = LCD_GetBackground();
	int i;
	for(i = 0; i < textureLength; i++) {
		LCD_DrawLine(texture[i].x0+offset_x, texture[i].y0+offset_y, texture[i].x1+offset_x, texture[i].y1+offset_y, bColor);
	}
}
