//ANIMATIONS
void idle() {
	if(idleStart==0){
		drawTextureByLine(idleStaticLines, idleStatLength, (MAX_X-idleWidth)/2, (MAX_Y-idleHeight)/2);
		drawTextureByLine(idle01Lines, idle01Length, (MAX_X-idleWidth)/2, (MAX_Y-idleHeight)/2);
		idleStart = 1;
	}
	if(idleStat==0){
		updateTextureByLine(idle01Lines, idle01Length, (MAX_X-idleWidth)/2, (MAX_Y-idleHeight)/2, idle02Lines, idle02Length, (MAX_X-idleWidth)/2, ((MAX_Y-idleHeight)/2)); //giustificata in basso
		idleStat = 1;
	}else if (idleStat == 1){
		updateTextureByLine(idle02Lines, idle02Length, (MAX_X-idleWidth)/2, ((MAX_Y-idleHeight)/2), idle01Lines, idle01Length, (MAX_X-idleWidth)/2, (MAX_Y-idleHeight)/2);
		idleStat = 0;
	}
}
void walkAndEat(uint8_t frameCounter) {
	uint16_t initialXOffset = (MAX_X-idleWidth)/2;
	uint16_t initialYOffset = (MAX_Y-idleHeight)/2;
	uint16_t distance = 10;
	if(frameCounter == 0){
		if(animation == 1){
			//happiness
			drawTextureByLine(happinessLines, happinessLength, initialXOffset+100, initialYOffset+35);
		}else if(animation == 2){
			drawTextureByLine(satietyLines, satietyLength, initialXOffset+100, initialYOffset+35);
		}
		if(idleStat == 0){
			eraseTextureByLine(idle01Lines, idle01Length, initialXOffset, initialYOffset);
		}else{
			eraseTextureByLine(idle02Lines, idle02Length, initialXOffset, initialXOffset);
		}
		updateTextureByLine(idleStaticLines, idleStatLength, initialXOffset, initialYOffset, walkStaticLines, walkStaticLength, initialXOffset+distance, initialYOffset);
		drawTextureByLine(walk01Lines, walk01Length, initialXOffset+distance, initialYOffset);
	}else if(frameCounter == 1){
		eraseTextureByLine(walkStaticLines, walkStaticLength, initialXOffset+distance, initialYOffset);
		updateTextureByLine(walk01Lines, walk01Length, initialXOffset+distance, initialYOffset, walkStaticLines, walkStaticLength, initialXOffset+distance*2, initialYOffset);
		drawTextureByLine(walk02Lines, walk02Length, initialXOffset+distance*2, initialYOffset);
	}else if(frameCounter == 2){
		eraseTextureByLine(walkStaticLines, walkStaticLength, initialXOffset+distance*2, initialYOffset);
		updateTextureByLine(walk02Lines, walk02Length, initialXOffset+distance*2, initialYOffset, walkStaticLines, walkStaticLength, initialXOffset+distance*3, initialYOffset);
		drawTextureByLine(walk01Lines, walk01Length, initialXOffset+distance*3, initialYOffset);
	}else if(frameCounter == 3){
		eraseTextureByLine(walkStaticLines, walkStaticLength, initialXOffset+distance*3, initialYOffset);
		updateTextureByLine(walk01Lines, walk01Length, initialXOffset+distance*3, initialYOffset, idle01Lines, idle01Length, initialXOffset+distance*4, initialYOffset);
		drawTextureByLine(idleStaticLines, idleStatLength, initialXOffset+distance*4, initialYOffset);
	}else if(frameCounter == 4) {
		eraseTextureByLine(idle01Lines, idle01Length, initialXOffset+distance*4, initialYOffset);
		updateTextureByLine(idleStaticLines, idleStatLength, initialXOffset+distance*4, initialYOffset, eatStaticLines, eatStaticLength, initialXOffset+distance*4, initialYOffset);
		drawTextureByLine(eat01Lines, eat01Length, initialXOffset+distance*4, initialYOffset);
	}else if(frameCounter == 5){
		if(animation == 1){
			eraseTextureByLine(happinessLines, happinessLength, initialXOffset+100, initialYOffset+35);
		}else if(animation == 2){
			eraseTextureByLine(satietyLines, satietyLength, initialXOffset+100, initialYOffset+35);
		}
		updateTextureByLine(eat01Lines, eat01Length, initialXOffset+distance*4, initialYOffset, eat02Lines, eat02Length, initialXOffset+distance*4, initialYOffset);
	}else if(frameCounter == 6){
		updateTextureByLine(eat02Lines, eat02Length, initialXOffset+distance*4, initialYOffset, eat01Lines, eat01Length, initialXOffset+distance*4, initialYOffset);
	}else if(frameCounter == 7){
		updateTextureByLine(eat01Lines, eat01Length, initialXOffset+distance*4, initialYOffset, eat02Lines, eat02Length, initialXOffset+distance*4, initialYOffset);
	}else if(frameCounter == 8){
		updateTextureByLine(eat02Lines, eat02Length, initialXOffset+distance*4, initialYOffset, eat01Lines, eat01Length, initialXOffset+distance*4, initialYOffset);
	}else if(frameCounter == 9){
		eraseTextureByLine(eat01Lines, eat01Length, initialXOffset+distance*4, initialYOffset);
		updateTextureByLine(eatStaticLines, eatStaticLength, initialXOffset+distance*4, initialYOffset, walkStaticLines, walkStaticLength, initialXOffset+distance*3, initialYOffset);
		drawTextureByLine(walk01Lines, walk01Length, initialXOffset+distance*3, initialYOffset);
	}else if(frameCounter == 10){
		eraseTextureByLine(walkStaticLines, walkStaticLength, initialXOffset+distance*3, initialYOffset);
		updateTextureByLine(walk01Lines, walk01Length, initialXOffset+distance*3, initialYOffset, walkStaticLines, walkStaticLength, initialXOffset+distance*2, initialYOffset);
		drawTextureByLine(walk02Lines, walk02Length, initialXOffset+distance*2, initialYOffset);
	}else if(frameCounter == 11){
		eraseTextureByLine(walkStaticLines, walkStaticLength, initialXOffset+distance*2, initialYOffset);
		updateTextureByLine(walk02Lines, walk02Length, initialXOffset+distance*2, initialYOffset, walkStaticLines, walkStaticLength, initialXOffset+distance, initialYOffset);
		drawTextureByLine(walk01Lines, walk01Length, initialXOffset+distance, initialYOffset);
	}else if(frameCounter == 12){
		eraseTextureByLine(walkStaticLines, walkStaticLength, initialXOffset+distance, initialYOffset);
		updateTextureByLine(walk01Lines, walk01Length, initialXOffset+distance, initialYOffset, idle01Lines, idle01Length, initialXOffset, initialYOffset);
		drawTextureByLine(idleStaticLines, idleStatLength, initialXOffset, initialYOffset);
		animation = 0;
	}
}
