void drawTime() {
	uint8_t tLenght = 8;
	updateTimeString();
	GUI_Text(MAX_X-xDistance-(tLenght*8), yDistance, (uint8_t *) timeString, White, LCD_GetBackground());
}

void drawStat(uint16_t xOffset, uint16_t yOffset, uint16_t xDistance, uint16_t yDistance, uint16_t side) {
	uint8_t i;
	uint16_t localXOffset = xOffset;
	uint16_t bColor = LCD_GetBackground();

	for(i=0;i<happiness;i++) {
		drawTextureByLine(happinessLines, happinessLength, localXOffset, yOffset+2);
		localXOffset+=side+xDistance;
	}
	localXOffset = xOffset;
	yOffset+=yDistance;
	for(i=0;i<satiety;i++) {
		drawTextureByLine(satietyLines, satietyLength, localXOffset, yOffset+2);
		localXOffset+=side+xDistance;
	}
}
void decreaseHappiness() {
	uint16_t bColor = LCD_GetBackground();
	if(happiness >= 0) { //controllo suprefluo?
		LCD_DrawRectangle(xOffset+(dim+xDistance)*happiness, yDistance*2+2, 12, 12, bColor);
		//eraseTextureByLine(happinessLines, happinessLength, xOffset+(dim+xDistance)*happiness, yDistance*3+2);
	}
}
void increaseHappiness() {
	//trigger per la walk e eat
	
	drawTextureByLine(happinessLines, happinessLength, xOffset+(dim+xDistance)*(happiness-1), yDistance*2+2);
}
void decreaseSatiety() {
	uint16_t bColor = LCD_GetBackground();
	if(satiety >= 0) {
		LCD_DrawRectangle(xOffset+(dim+xDistance)*satiety, yDistance*3+2, 12, 12, bColor);
		//eraseTextureByLine(satietyLines, satietyLength, xOffset+(dim+xDistance)*satiety, yDistance*2+2);
	}
}
void increaseSatiety() {
	drawTextureByLine(satietyLines, satietyLength, xOffset+(dim+xDistance)*(satiety-1), yDistance*3+2);
}
void drawGameOver() {
	LCD_Clear(Red);
	GUI_Text(84, MAX_Y/2+16, (uint8_t *) "GAME OVER", Red, White);
	GUI_Text(22, MAX_Y/2+32, (uint8_t *)"PRESS SELECT TO RESTART", White, Red);
}
void textureInit() {
	uint16_t bColor = LCD_GetBackground();
	LCD_Clear(bColor);
	animation = 0;
	idleStart = 0;
	GUI_Text(xDistance, yDistance, (uint8_t *) "Age: ", White, bColor);
	drawTime();
	GUI_Text(xDistance, yDistance*2, (uint8_t *) "Happiness: ", White, bColor);
	GUI_Text(xDistance, yDistance*3, (uint8_t *) "Satiety: ", White, bColor);
	drawStat(xOffset, yDistance*2, xDistance, yDistance, dim);
	drawMenu();
}
